import React from 'react';
import Form from 'react-bootstrap/Form';
// import FormControl from 'react-bootstrap/FormControl';

/**
 * Text Inputs will fill the width of their container
 *
 * @required props:
 *
 * @optional props:
 *    prepend // defaults to none
 *    placeholder // Jdefaults to none
 *    label // defaults to none. NOTE: recomended to use this for accesibility reasons
 *
 */

export default (props) => {
    function options() {
        let items = [];
        items.push(<option value='0'>Choose...</option>);
        if (props.options) {
            for (let i = 0; i < props.options.length; i++) {
                items.push(<option value={i + 1}>{props.options[i]}</option>);
            }
        }
        return items;
    }

    return (
        <Form inline>
            <Form.Label className='my-1 mr-2' htmlFor='inlineFormCustomSelectPref'>
                Preference
            </Form.Label>
            <Form.Control as='select' className='my-1 mr-sm-2' id='inlineFormCustomSelectPref' custom>
                {/* <option value='0'>Choose...</option>
                <option value='1'>One</option>
                <option value='2'>Two</option>
                <option value='3'>Three</option> */}
                {options()}
            </Form.Control>
        </Form>
    );
};
