import React from 'react';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';

// import './../../style/UTextInputStyle.css';

/**
 * Text Inputs will fill the width of their container
 *
 * @required props:
 *
 * @optional props:
 *    prepend // defaults to none
 *    placeholder // defaults to none
 *    label // defaults to placeholder
 *    number // if set, only allows numbers. defaults to false
 *
 */

export default (props) => {
    function prepend() {
        if (props.prepend) {
            return (
                <InputGroup.Prepend>
                    <InputGroup.Text id='basic-addon1'>{props.prepend}</InputGroup.Text>
                </InputGroup.Prepend>
            );
        } else {
            return;
        }
    }

    const label = `${props.label ? props.label : props.placeholder}`;

    return (
        <InputGroup onClick={() => props.onClick()}>
            {prepend()}
            <FormControl
                type='tel'
                placeholder={props.placeholder}
                aria-label={label}
                aria-describedby='basic-addon1'
            />
        </InputGroup>
        // <div className='InputAddOn'>
        //     <span className='InputAddOn-item'>$</span>
        //     <input
        //         className='InputAddOn-field'
        //         onClick={() => props.onClick()}
        //         placeholder={props.placeholder}
        //         aria-label={label}
        //         aria-describedby='basic-addon1'
        //     />
        // </div>
    );
};
