import React from 'react';
import Form from 'react-bootstrap/Form';

/**
 * Text Inputs will fill the width of their container
 *
 * @required props:
 *
 * @optional props:
 *    placeholder // defaults to none
 *    rows // defaults to
 *
 */

export default (props) => {
    const rows = () => {
        if (props.rows) {
            return props.rows;
        } else {
            return 3;
        }
    };

    return (
        <Form.Group>
            <Form.Label>{props.placeholder}</Form.Label>
            <Form.Control rows={rows} />
        </Form.Group>
    );
};
