import React from 'react';
import {useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import UTextInput from './utilities/UTextInput';
import USelectDropdown from './utilities/USelectDropdown';
import UButton from './utilities/UButton';
import UTextArea from './utilities/UTextArea';

import ImageUpload from './ImageUpload';

import './../style/CreateJobStyle.css';

export default () => {
    const [show, setShow] = useState(false);
    const [images, setImages] = useState([]);
    const [dimensions, setDimensions] = useState('');
    const [xDim, setXDim] = useState('');
    const [yDim, setYDim] = useState('');
    const [zDim, setZDim] = useState('');

    const handleClose = () => {
        setShow(false);
    };
    const handleShow = () => setShow(true);

    const units = ['inch', 'mm'];

    let changeDimensions = (e) => {
        setDimensions(xDim + 'x ' + yDim + 'x ' + zDim);
    };

    let changeXDim = (e) => {
        setXDim(e.target.value);
    };
    let changeYDim = (e) => {
        setYDim(e.target.value);
    };
    let changeZDim = (e) => {
        setZDim(e.target.value);
    };

    let getImages = (e) => {
        if (e) {
            let list = [];
            let files = e.target.files;
            for (let i = 0; i < files.length; i++) {
                let url = URL.createObjectURL(files[i]);
                list.push(url);
            }
            setImages(list);
        }
    };

    return (
        <Page>
            <Row>
                <Col className='mx-auto' lg={5}>
                    <Row className='mb-3'>
                        <UTextInput onClick={() => {}} placeholder='Model Name'></UTextInput>
                    </Row>
                    <Row>
                        <div className='mb-3'>
                            <div>
                                <span>3D Model File</span>
                            </div>
                            <input type='file' accept='.DAE, .OBJ, .STL, .X3D, .X3DB, .X3DV, .WRL, .3MF' />
                        </div>
                    </Row>
                    <Row>
                        <USelectDropdown placeholder='Filament' options={['one', 'two', 'three']}></USelectDropdown>
                    </Row>
                    <Row>
                        <USelectDropdown placeholder='Fill' options={['one', 'two', 'three']}></USelectDropdown>
                    </Row>
                    <Row>
                        <div className='mb-3 width-100'>
                            <UTextInput
                                value={dimensions}
                                onChange={(e) => changeDimensions(e)}
                                onClick={handleShow}
                                placeholder='Dimensions'></UTextInput>
                        </div>
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
                                <Modal.Title>Dimensions</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <Row className='mx-1'>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='1.5'
                                                className='my-0'
                                                value={xDim}
                                                onChange={(e) => changeXDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row>
                                            <Col>
                                                <span className='sub-label'>x-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col className='px-0 vertical-center'>
                                        <span className='horizontal-center'>X</span>
                                    </Col>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='2.0'
                                                value={yDim}
                                                onChange={(e) => changeYDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row className='sub-label'>
                                            <Col>
                                                <span className='sub-label'>y-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col className='px-0 vertical-center'>
                                        <span className='horizontal-center'>X</span>
                                    </Col>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='2.5'
                                                value={zDim}
                                                onChange={(e) => changeZDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row className='sub-label'>
                                            <Col>
                                                <span className='sub-label'>z-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col lg={'3'}>
                                        <USelectDropdown placeholder='Units' options={units} />
                                    </Col>
                                </Row>
                            </Modal.Body>
                            <Modal.Footer>
                                <UButton onClick={handleClose}>Set Dimensions</UButton>
                            </Modal.Footer>
                        </Modal>
                    </Row>
                    <Row className='shipping-details-area pt-3 px-3'>
                        <Row className='mx-1 mb-3 width-100'>
                            <span className='mb-1 ml-1'>Shipping Details</span>
                            <UTextInput onClick={() => {}} placeholder='Street Address'></UTextInput>
                        </Row>
                        <Row>
                            <Col className='ml-1 mb-3'>
                                <UTextInput onClick={() => {}} placeholder='City'></UTextInput>
                            </Col>
                            <Col className='mb-3'>
                                <UTextInput onClick={() => {}} placeholder='State'></UTextInput>
                            </Col>
                            <Col className='mr-1 mb-3'>
                                <UTextInput onClick={() => {}} placeholder='Zip'></UTextInput>
                            </Col>
                        </Row>
                    </Row>
                    <Row>
                        <div className='mt-3'>
                            <div>
                                <span>Images</span>
                            </div>
                            <input accept='image/*' for='image' type='file' multiple onChange={(e) => getImages(e)} />
                        </div>
                    </Row>
                    <Row className='mt-3'>
                        <UTextArea placeholder='Details'></UTextArea>
                    </Row>
                    <Row>
                        <UButton onClick={() => {}} size='lg'>
                            Create Model
                        </UButton>
                    </Row>
                </Col>
                <Col className='mx-auto' lg={5}>
                    <ImageUpload images={images} />
                </Col>
            </Row>
        </Page>
    );
};
