import React from 'react';
import {useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Toast from 'react-bootstrap/Toast';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import UTextInput from './utilities/UTextInput';
import USelectDropdown from './utilities/USelectDropdown';
import UButton from './utilities/UButton';
import UTextArea from './utilities/UTextArea';

import ImageUpload from './ImageUpload';

import './../style/CreateJobStyle.css';

export default () => {
    const [show, setShow] = useState(false);
    const [images, setImages] = useState([]);
    const [dimensions, setDimensions] = useState('');
    const [xDim, setXDim] = useState('');
    const [yDim, setYDim] = useState('');
    const [zDim, setZDim] = useState('');
    const [selectedUnits, setSelectedUnits] = useState('');
    const [showToast, setShowToast] = useState(true);

    const handleClose = () => {
        setShow(false);
        changeDimensions();
    };
    const handleShow = () => {
        setShow(true);
        document.activeElement.blur();
    };

    const units = ['in', 'mm'];
    const materials = [
        'Polyamide (SLS)',
        'Polyamide (MJF)',
        'Polyamide Priority (SLS)',
        'Alumide',
        'Polypropylene',
        'Rubber Like (MJF)',
        'Steel',
        'High-Detail Stainless Steel',
        'Multicolor+',
        'Standard Resin',
        'Gray Resin',
        'Mammoth Resin',
        'Transparent Resin',
        'Silver',
        'Gold',
        'Brass',
        'Bronze',
        'Copper',
        'Titanium',
        'Aluminum',
        'ABS',
        'High-Detail Resin',
    ];

    let changeSelectedUnits = (e) => {
        setSelectedUnits(units[e.target.value - 1]);
    };

    let changeDimensions = () => {
        if (xDim && yDim && zDim && selectedUnits) {
            setDimensions(xDim + selectedUnits + ' x ' + yDim + selectedUnits + ' x ' + zDim + selectedUnits);
        }
    };

    let changeXDim = (e) => {
        setXDim(e.target.value);
    };
    let changeYDim = (e) => {
        setYDim(e.target.value);
    };
    let changeZDim = (e) => {
        setZDim(e.target.value);
    };

    let getImages = (e) => {
        if (e) {
            let list = [];
            let files = e.target.files;
            for (let i = 0; i < files.length; i++) {
                let url = URL.createObjectURL(files[i]);
                list.push(url);
            }
            setImages(list);
        }
    };

    return (
        <Page>
            <div className='toast-container'>
                <Toast show={showToast} onClose={setShowToast(false)} className='toast'>
                    <Toast.Header>
                        <strong className='mr-auto'>Alert!</strong>
                    </Toast.Header>
                    <Toast.Body>Only five (5) images may be uploaded</Toast.Body>
                </Toast>
            </div>

            <Row>
                <Col className='mx-auto' lg={5}>
                    <Row className='mb-3'>
                        <UTextInput onChange={() => {}} onClick={() => {}} placeholder='Model Name'></UTextInput>
                    </Row>
                    <Row>
                        <div className='mb-3'>
                            <div>
                                <span>3D Model File</span>
                            </div>
                            <input type='file' accept='.DAE, .OBJ, .STL, .X3D, .X3DB, .X3DV, .WRL, .3MF' />
                        </div>
                    </Row>
                    <Row>
                        <USelectDropdown placeholder='Filament' options={materials}></USelectDropdown>
                    </Row>
                    <Row>
                        <div className='mb-3 width-100'>
                            <UTextInput
                                append='%'
                                number
                                onChange={() => {}}
                                onClick={() => {}}
                                placeholder='Fill'></UTextInput>
                        </div>
                    </Row>
                    <Row>
                        <div className='mb-3 width-100'>
                            <UTextInput
                                value={dimensions}
                                onChange={() => {}}
                                onClick={handleShow}
                                placeholder='Dimensions'></UTextInput>
                        </div>
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
