import React from 'react';
import {useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Toast from 'react-bootstrap/Toast';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import UTextInput from './utilities/UTextInput';
import USelectDropdown from './utilities/USelectDropdown';
import UButton from './utilities/UButton';
import UTextArea from './utilities/UTextArea';

import ImageUpload from './ImageUpload';

import './../style/CreateJobStyle.css';

export default () => {
    const [images, setImages] = useState([]);
    const [dimensions, setDimensions] = useState('');
    const [xDim, setXDim] = useState('');
    const [yDim, setYDim] = useState('');
    const [zDim, setZDim] = useState('');
    const [selectedUnits, setSelectedUnits] = useState('');
    const [fill, setFill] = useState('');
    const [filament, setFilament] = useState('');
    const [modelName, setModelName] = useState('');
    const [details, setDetails] = useState('');
    const [streetAddress, setStreetAddress] = useState('');
    const [city, setCity] = useState('');
    const [state, setState] = useState('');
    const [zip, setZip] = useState('');

    const [showToast, setShowToast] = useState(false);
    const [show, setShow] = useState(false);

    const handleClose = () => {
        setShow(false);
        changeDimensions();
    };
    const handleShow = () => {
        setShow(true);
        document.activeElement.blur();
    };

    const units = ['in', 'mm'];
    const materials = [
        'Polyamide (SLS)',
        'Polyamide (MJF)',
        'Polyamide Priority (SLS)',
        'Alumide',
        'Polypropylene',
        'Rubber Like (MJF)',
        'Steel',
        'High-Detail Stainless Steel',
        'Multicolor+',
        'Standard Resin',
        'Gray Resin',
        'Mammoth Resin',
        'Transparent Resin',
        'Silver',
        'Gold',
        'Brass',
        'Bronze',
        'Copper',
        'Titanium',
        'Aluminum',
        'ABS',
        'High-Detail Resin',
    ];

    let changeSelectedUnits = (e) => {
        setSelectedUnits(units[e.target.value - 1]);
    };

    let changeDimensions = () => {
        if (xDim && yDim && zDim && selectedUnits) {
            setDimensions(xDim + selectedUnits + ' x ' + yDim + selectedUnits + ' x ' + zDim + selectedUnits);
        }
    };

    let changeXDim = (e) => {
        setXDim(e.target.value);
    };
    let changeYDim = (e) => {
        setYDim(e.target.value);
    };
    let changeZDim = (e) => {
        setZDim(e.target.value);
    };

    let getImages = (e) => {
        if (e) {
            let list = [];
            let files = e.target.files;
            for (let i = 0; i < files.length; i++) {
                let url = URL.createObjectURL(files[i]);
                list.push(url);
            }
            setImages(list);
            if (list.length > 5) {
                setShowToast(true);
            }
        }
    };

    const createJob = () => {
        let job = {
            title: modelName,
            filament: filament,
            fill: fill,
            printDimensionX: xDim,
            printDimensionY: yDim,
            printDimensionZ: zDim,
            dimensionUnit: selectedUnits,
            primaryImage: 'TODO: place primary image url here',
            images: ['TODO: place image urls here'],
            model: 'TODO: place model file url here',
            details: details,
            streetAddress: streetAddress,
            city: city,
            state: state,
            zip: zip,
            token: 'TODO: place token here',
        };

        console.log(job);
    };

    return (
        <Page>
            <div className='toast-container'>
                <Toast show={showToast} onClose={() => setShowToast(false)} className='toast'>
                    <Toast.Header>
                        <strong className='mr-auto'>Alert!</strong>
                    </Toast.Header>
                    <Toast.Body>Only five (5) images may be uploaded</Toast.Body>
                </Toast>
            </div>

            <Row>
                <Col className='mx-auto' lg={5}>
                    <Row className='mb-3'>
                        <UTextInput
                            value={modelName}
                            onChange={(e) => {
                                setModelName(e.target.value);
                            }}
                            onClick={() => {}}
                            placeholder='Model Name'></UTextInput>
                    </Row>
                    <Row>
                        <div className='mb-3'>
                            <div>
                                <span>3D Model File</span>
                            </div>
                            <input type='file' accept='.DAE, .OBJ, .STL, .X3D, .X3DB, .X3DV, .WRL, .3MF' />
                        </div>
                    </Row>
                    <Row>
                        <USelectDropdown
                            value={filament}
                            onChange={(e) => {
                                setFilament(materials[e.target.value - 1]);
                            }}
                            placeholder='Filament'
                            options={materials}></USelectDropdown>
                    </Row>
                    <Row>
                        <div className='mb-3 width-100'>
                            <UTextInput
                                append='%'
                                number
                                value={fill}
                                onChange={(e) => {
                                    setFill(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Fill'></UTextInput>
                        </div>
                    </Row>
                    <Row>
                        <div className='mb-3 width-100'>
                            <UTextInput
                                value={dimensions}
                                onChange={() => {}}
                                onClick={handleShow}
                                placeholder='Dimensions'></UTextInput>
                        </div>
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
                                <Modal.Title>Dimensions</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <Row className='mx-1'>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='1.5'
                                                className='my-0'
                                                value={xDim}
                                                onChange={(e) => changeXDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row>
                                            <Col>
                                                <span className='sub-label'>x-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col className='px-0 vertical-center'>
                                        <span className='horizontal-center'>X</span>
                                    </Col>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='2.0'
                                                value={yDim}
                                                onChange={(e) => changeYDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row className='sub-label'>
                                            <Col>
                                                <span className='sub-label'>y-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col className='px-0 vertical-center'>
                                        <span className='horizontal-center'>X</span>
                                    </Col>
                                    <Col>
                                        <Row>
                                            <UTextInput
                                                number
                                                placeholder='2.5'
                                                value={zDim}
                                                onChange={(e) => changeZDim(e)}
                                                onClick={() => {}}
                                            />
                                        </Row>
                                        <Row className='sub-label'>
                                            <Col>
                                                <span className='sub-label'>z-axis</span>
                                            </Col>
                                        </Row>
                                    </Col>
                                    <Col lg={'3'}>
                                        <USelectDropdown
                                            onChange={(e) => changeSelectedUnits(e)}
                                            value={selectedUnits}
                                            placeholder='Units'
                                            options={units}
                                        />
                                    </Col>
                                </Row>
                            </Modal.Body>
                            <Modal.Footer>
                                <UButton onClick={handleClose}>Set Dimensions</UButton>
                            </Modal.Footer>
                        </Modal>
                    </Row>
                    <Row className='shipping-details-area pt-3 px-3'>
                        <Row className='mx-1 mb-3 width-100'>
                            <span className='mb-1 ml-1'>Shipping Details</span>
                            <UTextInput
                                value={streetAddress}
                                onChange={(e) => {
                                    setStreetAddress(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Street Address'></UTextInput>
                        </Row>
                        <Row>
                            <Col className='ml-1 mb-3'>
                                <UTextInput
                                    value={city}
                                    onChange={(e) => {
                                        setCity(e.target.value);
                                    }}
                                    onClick={() => {}}
                                    placeholder='City'></UTextInput>
                            </Col>
                            <Col className='mb-3'>
                                <UTextInput
                                    value={state}
                                    onChange={(e) => {
                                        setState(e.target.value);
                                    }}
                                    onClick={() => {}}
                                    placeholder='State'></UTextInput>
                            </Col>
                            <Col className='mr-1 mb-3'>
                                <UTextInput
                                    value={zip}
                                    onChange={(e) => {
                                        setZip(e.target.value);
                                    }}
                                    onClick={() => {}}
                                    placeholder='Zip'></UTextInput>
                            </Col>
                        </Row>
                    </Row>
                    <Row>
                        <div className='mt-3'>
                            <div>
                                <span>Images</span>
                            </div>
                            <input
                                accept='image/*'
                                htmlFor='image'
                                type='file'
                                multiple
                                onChange={(e) => getImages(e)}
                            />
                        </div>
                    </Row>
                    <Row className='mt-3'>
                        <UTextArea
                            value={details}
                            onChange={(e) => {
                                setDetails(e.target.value);
                            }}
                            placeholder='Details'></UTextArea>
                    </Row>
                    <Row>
                        <UButton onClick={() => createJob()} size='lg'>
                            Create Model
                        </UButton>
                    </Row>
                </Col>

                <Col className='mx-auto' lg={5}>
                    <ImageUpload images={images} />
                </Col>
            </Row>
        </Page>
    );
};
