import React from 'react';
import {useState} from 'react';
import axios from 'axios';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import ULogo from './utilities/ULogo';
import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';

import './../style/LoginStyle.css';
import ULink from './utilities/ULink';

export default () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [token, setToken] = useState('');

    const terms = <ULink to='/terms-of-service'>Terms of Service</ULink>;

    const signUp = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/register',
            data: {
                email: email,
                password: password,
                first_name: firstName,
                last_name: lastName,
            },
        }).then((response) => {
            setToken(response.data.token);
        });
    };

    return (
        <Page>
            <Row>
                <Col lg={6} className='mt-5'>
                    <Row className='mb-3'>
                        <Col className='ml-auto' lg={9}>
                            <span className='welcome-title'>
                                <strong>Sign Up</strong> Unite3D
                            </span>
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={firstName}
                                onChange={(e) => {
                                    setFirstName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='First Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={lastName}
                                onChange={(e) => {
                                    setLastName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Last Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={email}
                                onChange={(e) => {
                                    setEmail(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Email'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={password}
                                password
                                onChange={(e) => {
                                    setPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={confirmPassword}
                                password
                                onChange={(e) => {
                                    setConfirmPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Confirm Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <Row className='modeler-vendor-area mx-1'>
                                <Row className='mx-0 my-2'>
                                    <Col className=''>
                                        <span className=''>What is your purpose in signing up? </span>
                                        <br />
                                        <span>
                                            Print my model <strong>(Modeler)</strong>{' '}
                                            <input type='radio' value='Modeler' name='purpose' />
                                        </span>
                                        <br />
                                        <span>
                                            Print other people's models <strong>(Vendor)</strong>{' '}
                                            <input type='radio' value='Vendor' name='purpose' />
                                        </span>
                                    </Col>
                                </Row>
                                <Row className='mx-1 my-2'>
                                    <Col>
                                        <span className='register-printer-title'>Register Printer</span>
                                        <UTextInput
                                            value={''}
                                            onChange={() => {}}
                                            onClick={() => {}}
                                            placeholder='Make'
                                        />
                                        <UTextInput
                                            value={''}
                                            onChange={() => {}}
                                            onClick={() => {}}
                                            placeholder='Model'
                                        />
                                    </Col>
                                </Row>
                            </Row>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <input name='agree' type='checkbox' />
                            <span> Agree to&nbsp;{terms}</span>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <UButton onClick={() => signUp()}>Sign Up</UButton>
                        </Col>
                    </Row>
                </Col>
                <Col lg={4}>
                    <ULogo height={'400px'} color='purple' />
                </Col>
            </Row>
        </Page>
    );
};
