import React from 'react';
import {useState} from 'react';
import axios from 'axios';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import ULogo from './utilities/ULogo';
import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';

import './../style/LoginStyle.css';
import ULink from './utilities/ULink';

export default () => {
    const hereLink = <ULink to='/register'>here</ULink>;
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState('');

    const login = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/login',
            data: {
                email: email,
                password: password,
            },
        }).then((response) => {
            setToken(response.data.token);
            console.log(token);
        });
    };

    return (
        <Page>
            <Row>
                <Col lg={6} className='mt-5'>
                    <Row className='mb-3'>
                        <Col className='ml-auto' lg={9}>
                            <span className='welcome-title'>
                                <strong>Welcome back to</strong> Unite3D
                            </span>
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                onClick={() => {}}
                                placeholder='Email'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={password}
                                onChange={(e) => {
                                    setPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                password
                                placeholder='Password'
                            />
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <UButton onClick={() => login()}>Login</UButton>
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto pr-5' lg={9}>
                            Need an account? Register&nbsp; {hereLink}
                        </Col>
                    </Row>
                </Col>
                <Col lg={4}>
                    <ULogo height={'400px'} color='purple' />
                </Col>
            </Row>
        </Page>
    );
};
