import React from 'react';
import Button from 'react-bootstrap/Button';
import './../style/UStyle.css'


export default class UButton extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      actionBlink: false,
      signInBlink: false,
      blackBlink: false,
      whiteBlink: false
    }

    if (props.disabled) {
      this.variant = 'u-disabled'
    } else {
      this.variant = props.variant || 'u-action'
    }

    this.content = props.content
    this.size = props.size
    this.disabled = props.disabled
    this.centered = props.centered
    console.log(this.size)
  }

  click () {
    console.log(this.variant.includes('white'))
    if (this.variant.includes('action')) {
      this.setState({ actionBlink: true })
    } else if (this.variant.includes('black')) {
      this.setState({ blackBlink: true })
    }else if (this.variant.includes('white')) {
      this.setState({ whiteBlink: true })
    } else {
      this.setState({ signInBlink: true })
    }
  }
  animationEnded () {
    if (this.variant.includes('action')) {
      this.setState({ actionBlink: false })
    } else if (this.variant.includes('black')) {
      this.setState({ blackBlink: false })
    }else if (this.variant.includes('white')) {
      this.setState({ whiteBlink: false })
    } else {
      this.setState({ signInBlink: false })
    }
  }

  render() {
    let classes = `
      ${this.centered ? 'centered' : ''} 
      ${this.state.actionBlink ? 'action-blink' : ''}
      ${this.state.signInBlink ? 'sign-in-blink' : ''}
      ${this.state.blackBlink ? 'black-blink' : ''}
      ${this.state.whiteBlink ? 'white-blink' : ''}
    `
    return (
      <Button
        onClick={() => this.click()}
        onAnimationEnd={() => this.animationEnded()}
        className={classes}
        disabled={this.disabled}
        size={this.size}
        variant={this.variant}
      >
        {this.content}
      </Button>
    )
  }
}
