import React from 'react';
import BrowsingNav from './BrowsingNav';
import ImageCard from './ImageCard';
import '../style/Browsing.css';
import {
    getMyModels,
    getMyOffers,
    getMyJobsInProgress,
    getMyCompletedPrints,
    getNewJobs,
    getMyBids,
    getCurrentJobs,
    getPastJobs,
} from '../Backend';

export default class BrowsingManger extends React.Component {
    constructor(props) {
        super(props);
        let modelerNavItems = [
            {
                title: 'Models',
                api: getMyModels,
            },
            {
                title: 'My Offers',
                api: getMyOffers,
            },
            {
                title: 'Jobs In Progress',
                api: getMyJobsInProgress,
            },
            {
                title: 'Completed Prints',
                api: getMyCompletedPrints,
            },
        ];

        let vendorNavItems = [
            {
                title: 'New Jobs',
                api: getNewJobs,
            },
            {
                title: 'My Bids',
                api: getMyBids,
            },
            {
                title: 'Current Jobs',
                api: getCurrentJobs,
            },
            {
                title: 'Past Jobs',
                api: getPastJobs,
            },
        ];

        this.state = {
            imageCards: [],
            selectedNavigation: '',
            selectedApi: props.iAmModeler ? getMyModels : getNewJobs,
            navItems: props.iAmModeler ? modelerNavItems : vendorNavItems,
        };
    }

    componentDidMount() {
        this.gatherModels();
    }

    gatherModels() {
        let modelInfo = this.state.selectedApi();
        let imageCards = [];
        let id = 1;
        modelInfo.forEach((model) => {
            imageCards.push(
                <ImageCard
                    title={model.title}
                    value={model.value}
                    content={'Filament: ' + model.filament + ' DIM: ' + model.dim + ' ' + model.units}
                    image={model.base64Image}
                    key={id}
                />,
            );
            id += 1;
        });
        this.setState({imageCards: imageCards});
    }

    handleNavClick = (navItem) => {
        this.setState(
            {
                selectedApi: navItem.api,
            },
            this.gatherModels, // callback.. notice it doesn't have the () because we're not executing it, we're passing it in.
        );
    };

    render() {
        if (!this.state.imageCards.length) {
            return (
                <BrowsingNav
                    className='browsing-nav'
                    navItems={this.state.navItems}
                    handleClick={this.handleNavClick}
                />
            );
        }
        return (
            <div>
                <BrowsingNav
                    className='browsing-nav'
                    navItems={this.state.navItems}
                    handleClick={this.handleNavClick}
                />
                <section className='browsing-grid'>{this.state.imageCards}</section>
            </div>
        );
    }
}
