import React from 'react';
import Button from 'react-bootstrap/Button';
import './../style/UStyle.css'


export default class UButton extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      actionBlink: false,
      signInBlink: false
    }

    if (props.disabled) {
      this.variant = 'u-disabled'
    } else {
      this.variant = props.variant
    }

    this.content = props.content
    this.size = props.size
    this.disabled = props.disabled
    this.centered = props.centered
  }

  click () {
    if (!this.variant.includes('sign')) {
      this.setState({ actionBlink: true })
    } else {
      this.setState({ signInBlink: true })
    }
  }
  animationEnded () {
    if (!this.variant.includes('sign')) {
      this.setState({ actionBlink: false })
    } else {
      this.setState({ signInBlink: false })
    }
  }

  render() {
    let classes = `
      ${this.centered ? 'centered' : ''} 
      ${this.state.actionBlink ? 'action-blink' : ''}
      ${this.state.signInBlink ? 'sign-in-blink' : ''}
    `
    return (
      <Button
        onClick={() => this.click()}
        onAnimationEnd={() => this.animationEnded()}
        className={classes}
        disabled={this.disabled}
        size={this.size}
        variant={this.variant}
      >
        {this.content}
      </Button>
    )
  }
}
