import React from 'react';
import {useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import UTextInput from './utilities/UTextInput';
import USelectDropdown from './utilities/USelectDropdown';
import UButton from './utilities/UButton';
import UTextArea from './utilities/UTextArea';

import ImageUpload from './ImageUpload';

import './../style/CreateJobStyle.css';

// export default class CreateJob extends React.Component {
export default () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    let images = [];

    let getImages = (e) => {
        if (e) {
            console.log(e.target.files);
            images = e.target.files;
        }
    };

    return (
        <Page>
            <Row>
                <Col className='mx-auto' lg={5}>
                    <Row>
                        <UTextInput onClick={() => {}} placeholder='Model Name'></UTextInput>
                    </Row>
                    <Row>
                        <div className='mb-3'>
                            {/* <UButton onClick={() => {}} variant={'secondary'} size={'sm'}>
                                Select File
                            </UButton> */}
                            <input type='file' multiple onChange={getImages()} />
                        </div>
                    </Row>
                    <Row>
                        <USelectDropdown placeholder='Filament' options={['one', 'two', 'three']}></USelectDropdown>
                    </Row>
                    <Row>
                        <USelectDropdown placeholder='Fill' options={['one', 'two', 'three']}></USelectDropdown>
                    </Row>
                    <Row>
                        <UTextInput onClick={handleShow} placeholder='Dimensions'></UTextInput>
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
                                <Modal.Title>Dimensions</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <span>TODO</span>
                            </Modal.Body>
                            <Modal.Footer>
                                <UButton onClick={handleClose}>Set Dimensions</UButton>
                            </Modal.Footer>
                        </Modal>
                    </Row>
                    <Row className='shipping-details-area pt-3 px-3'>
                        <div className='mb-1 ml-1'>
                            <span>Shipping Details</span>
                        </div>
                        <UTextInput onClick={() => {}} placeholder='Street Address'></UTextInput>
                        <Col className='pl-0'>
                            <UTextInput onClick={() => {}} placeholder='City'></UTextInput>
                        </Col>
                        <Col>
                            <UTextInput onClick={() => {}} placeholder='State'></UTextInput>
                        </Col>
                        <Col className='pr-0'>
                            <UTextInput onClick={() => {}} placeholder='Zip'></UTextInput>
                        </Col>
                    </Row>
                    <Row className='mt-3'>
                        <UTextArea placeholder='Details'></UTextArea>
                    </Row>
                    <Row>
                        <UButton onClick={() => {}} size='lg'>
                            Create Model
                        </UButton>
                    </Row>
                </Col>
                <Col className='mx-auto' lg={5}>
                    <ImageUpload images={images} />
                </Col>
            </Row>
        </Page>
    );
};
