 *
 * @optional props:
 *    placeholder // defaults to none
 *    rows // defaults to
 *
 */

export default (props) => {
    return (
        <Form.Group>
            <Form.Label>{props.placeholder}</Form.Label>
            <Form.Control rows={props.rows} />
        </Form.Group>
    );
};
