import React from 'react';
import {Multiselect} from 'multiselect-react-dropdown';

import './../../style/UMultiselectStyle.css';
/**
 * Select Dropdowns will fill the width of their container
 *
 * @required props:
 *    options // the list the dropdown will contain
 *    onChange
 *
 * @optional props:
 *    placeholder // defaults to 'Choose...'
 *
 */

export default (props) => {
    const options = () => {
        let items = [];
        if (props.options) {
            for (let i = 1; i <= props.options.length; i++) {
                items.push({name: props.options[i], id: i});
            }
        }
        return items;
    };

    return (
        <div className='select-container'>
            <Multiselect
                options={options()} // Options to display in the dropdown
                selectedValues={props.selectedValues} // Preselected value to persist in dropdown
                onSelect={props.onSelect} // Function will trigger on select event
                onRemove={props.onRemove} // Function will trigger on remove event
                displayValue={props.displayValue} // Property name to display in the dropdown options
            />
        </div>
    );
};
