import React from 'react';
import axios from 'axios';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import ULogo from './utilities/ULogo';
import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';

import './../style/LoginStyle.css';
import ULink from './utilities/ULink';
import MultiSelect from 'react-multi-select-component'; // https://www.npmjs.com/package/react-multi-select-component

import config from './../static/config';
import UDimensionsModal from './utilities/UDimensionsModal';

export default class CreateAccount extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            confirmPassword: '',
            firstName: '',
            lastName: '',
            isVendor: false,
            token: '',
            selectedFilaments: [],
            printerMake: '',
            printerModel: '',

            show: false,
            dimensions: '',
            xDim: '',
            yDim: '',
            zDim: '',
            selectedUnits: '',
        };
        // this.setState({email: 'newemail'}, () => {})
        //this.state.email
    }

    terms = (<ULink to='/terms-of-service'>Terms of Service</ULink>);

    signUp = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/register',
            data: {
                email: this.state.email,
                password: this.state.password,
                first_name: this.state.firstName,
                last_name: this.state.lastName,
                isVendor: this.state.isVendor,
            },
        }).then((response) => {
            setToken(response.data.token).then(() => {
                if (this.state.isVendor) {
                    registerPrinter();
                }
            });
        });
    };

    registerPrinter = () => {
        let userId = '';
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/verify',
            data: {
                token: this.state.token,
            },
        }).then((response) => {
            userId = response.data.user.id;
        });
        if (userId) {
            axios({
                method: 'post',
                url: 'http://localhost:8000/printer/register',
                data: {
                    vendor: userId,
                    make: this.state.printerMake,
                    model: this.state.printerModel,
                    filaments: this.state.selectedFilaments,
                    printDimensionX: this.state.xDim,
                    printDimensionY: this.state.yDim,
                    printDimensionZ: this.state.zDim,
                    dimensionUnit: this.state.selectedUnits,
                },
            })
                .then((response) => {
                    alert('TODO: this endpoint has not been made/tested');
                })
                .catch((err) => {
                    alert('TODO: this endpoint has not been made/tested');
                });
        }
    };

    materials = config.materials;
    options = () => {
        let op = [];
        for (let i = 0; i < materials.length - 1; i++) {
            op.push({label: materials[i], value: materials[i]});
        }
        return op;
    };

    getPrinterRegistrationForm = () => {
        if (this.state.isVendor) {
            return (
                <Row width='100%' className='mx-0 mb-3 width-100'>
                    <Col>
                        <span className='register-printer-title'>Register Printer</span>
                        <UTextInput
                            value={this.state.printerMake}
                            onChange={(e) => setPrinterMake(e.target.value)}
                            onClick={() => {}}
                            placeholder='Make'
                        />
                        <UTextInput
                            value={this.state.printerModel}
                            onChange={(e) => this.setState({printerModel: e.taget.value})}
                            onClick={() => {}}
                            placeholder='Model'
                        />
                        <UTextInput
                            value={this.state.modalData.dimensions}
                            onChange={() => {}}
                            onClick={() => handleShow()}
                            placeholder='Build Size'></UTextInput>
                        <label /> Supported Filament
                        <MultiSelect
                            options={options()}
                            value={this.state.selectedFilaments}
                            onChange={this.setState({printerModel: e.taget.value})}
                            labelledBy={'Supported Filament'}
                        />
                    </Col>
                </Row>
            );
        } else {
            return <></>;
        }
    };

    // Modal stuff

    handleShow = () => {
        this.setState({show: true});
        document.activeElement.blur();
    };
    handleClose = () => {
        this.setState({show: false});
        changeDimensions();
    };
    changeDimensions = () => {
        if (
            this.state.modalData.xDim &&
            this.state.modalData.yDim &&
            this.state.modalData.zDim &&
            this.state.modalData.selectedUnits
        ) {
            this.setState({
                dimesions:
                    this.state.xDim +
                    this.state.selectedUnits +
                    ' x ' +
                    this.state.yDim +
                    this.state.selectedUnits +
                    ' x ' +
                    this.state.zDim +
                    this.state.selectedUnits,
            });
        }
    };
    render() {
        return (
            <Page>
                <Row>
                    <Col lg={6} className='mt-5'>
                        <Row className='mb-3'>
                            <Col className='ml-auto' lg={9}>
                                <span className='welcome-title'>
                                    <strong>Sign Up</strong> Unite3D
                                </span>
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <UTextInput
                                    value={this.state.firstName}
                                    onChange={(e) => {
                                        this.setState({firstName: e.target.value});
                                    }}
                                    onClick={() => {}}
                                    placeholder='First Name'
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <UTextInput
                                    value={this.state.lastName}
                                    onChange={(e) => {
                                        this.setState({lastName: e.target.value});
                                    }}
                                    onClick={() => {}}
                                    placeholder='Last Name'
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <UTextInput
                                    value={this.state.email}
                                    onChange={(e) => {
                                        this.setState({email: e.target.value});
                                    }}
                                    onClick={() => {}}
                                    placeholder='Email'
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <UTextInput
                                    value={this.state.password}
                                    password
                                    onChange={(e) => {
                                        this.setState({password: e.target.value});
                                    }}
                                    onClick={() => {}}
                                    placeholder='Password'
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <UTextInput
                                    value={this.state.confirmPassword}
                                    password
                                    onChange={(e) => {
                                        this.setState({confirmPassword: e.target.value});
                                    }}
                                    onClick={() => {}}
                                    placeholder='Confirm Password'
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col className='ml-auto mb-3 pr-5' lg={9}>
                                <Row className='modeler-vendor-area mx-1'>
                                    <Row className='mx-0 my-3 width-100'>
                                        <Col className=''>
                                            <span className=''>What is your purpose in signing up? </span>
                                            <br />
                                            <span>
                                                Print my model <strong>(Modeler)</strong>{' '}
                                                <input
                                                    onChange={() => this.setState({isVendor: false})}
                                                    type='radio'
                                                    name='purpose'
                                                />
                                            </span>
                                            <br />
                                            <span>
                                                Print other people's models <strong>(Vendor)</strong>{' '}
                                                <input
                                                    onChange={() => this.setState({isVendor: true})}
                                                    type='radio'
                                                    name='purpose'
                                                />
                                            </span>
                                        </Col>
                                    </Row>
                                    {getPrinterRegistrationForm()}
                                    <UDimensionsModal
                                        xDim={this.state.modalData.xDim}
                                        setXDim={(e) => this.setState({xDim: e.target.value})}
                                        yDim={this.state.modalData.yDim}
                                        setYDim={(e) => this.setState({yDim: e.target.value})}
                                        zDim={this.state.modalData.zDim}
                                        setZDim={(e) => this.setState({zDim: e.target.value})}
                                        selectedUnits={this.state.modalData.selectedUnits}
                                        setSelectedUnits={(e) => this.setState({selectedUnits: e.target.value})}
                                        show={this.state.modalData.show}
                                        handleClose={() => handleClose}
                                    />
                                </Row>
                            </Col>
                        </Row>
                        <Row className='mb-3'>
                            <Col className='ml-auto pr-5' lg={9}>
                                <input name='agree' type='checkbox' />
                                <span> Agree to&nbsp;{terms}</span>
                            </Col>
                        </Row>
                        <Row className='mb-3'>
                            <Col className='ml-auto pr-5' lg={9}>
                                <UButton onClick={() => signUp()}>Sign Up</UButton>
                            </Col>
                        </Row>
                    </Col>
                    <Col lg={4}>
                        <ULogo height={'400px'} color='purple' />
                    </Col>
                </Row>
            </Page>
        );
    }
}
