import React from 'react';
import {useState} from 'react';

// NOTE: Modal is throwing a warining ->
//      "Warning: findDOMNode is deprecated in StrictMode.
//      findDOMNode was passed an instance of Transition which is inside StrictMode.
//      Instead, add a ref directly to the element you want to reference.
//      Learn more about using refs safely here: https://fb.me/react-strict-mode-find-node"
// I've looked into it and it seems that it is an actual issue with the way that Modal is implemented
// under the hood. They are currently working on fixing it.
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';
import USelectDropdown from './utilities/USelectDropdown';
/**
 * Select Dropdowns will fill the width of their container
 *
 * @required props:
 *
 *
 * @optional props:
 *
 */

export default (props) => {
    const [xDim, setXDim] = useState('');
    const [yDim, setYDim] = useState('');
    const [zDim, setZDim] = useState('');
    const [show, setShow] = useState(false);
    const [selectedUnits, setSelectedUnits] = useState('');
    const [dimensions, setDimensions] = useState('');
    const units = ['in', 'mm'];

    let changeSelectedUnits = (e) => {
        setSelectedUnits(units[e.target.value - 1]);
    };
    const handleClose = () => {
        setShow(false);
        changeDimensions();
    };
    const handleShow = () => {
        setShow(true);
        document.activeElement.blur();
    };

    return (
        <Modal show={show} onHide={() => handleClose()}>
            <Modal.Header closeButton>
                <Modal.Title>Dimensions</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row className='mx-1'>
                    <Col>
                        <Row>
                            <UTextInput
                                number
                                placeholder='1.5'
                                className='my-0'
                                value={xDim}
                                onChange={(e) => {
                                    setXDim(e.target.value);
                                }}
                                onClick={() => {}}
                            />
                        </Row>
                        <Row>
                            <Col>
                                <span className='sub-label'>x-axis</span>
                            </Col>
                        </Row>
                    </Col>
                    <Col className='px-0 vertical-center'>
                        <span className='horizontal-center'>X</span>
                    </Col>
                    <Col>
                        <Row>
                            <UTextInput
                                number
                                placeholder='2.0'
                                value={yDim}
                                onChange={(e) => {
                                    setYDim(e.target.value);
                                }}
                                onClick={() => {}}
                            />
                        </Row>
                        <Row className='sub-label'>
                            <Col>
                                <span className='sub-label'>y-axis</span>
                            </Col>
                        </Row>
                    </Col>
                    <Col className='px-0 vertical-center'>
                        <span className='horizontal-center'>X</span>
                    </Col>
                    <Col>
                        <Row>
                            <UTextInput
                                number
                                placeholder='2.5'
                                value={zDim}
                                onChange={(e) => {
                                    setZDim(e.target.value);
                                }}
                                onClick={() => {}}
                            />
                        </Row>
                        <Row className='sub-label'>
                            <Col>
                                <span className='sub-label'>z-axis</span>
                            </Col>
                        </Row>
                    </Col>
                    <Col lg={'3'}>
                        <USelectDropdown
                            onChange={(e) => changeSelectedUnits(e)}
                            value={selectedUnits}
                            placeholder='Units'
                            options={units}
                        />
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer>
                <UButton onClick={handleClose}>Set Dimensions</UButton>
            </Modal.Footer>
        </Modal>
    );
};
