import React from 'react';
import {useState} from 'react';
import axios from 'axios';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

// NOTE: Modal is throwing a warining ->
//      "Warning: findDOMNode is deprecated in StrictMode.
//      findDOMNode was passed an instance of Transition which is inside StrictMode.
//      Instead, add a ref directly to the element you want to reference.
//      Learn more about using refs safely here: https://fb.me/react-strict-mode-find-node"
// I've looked into it and it seems that it is an actual issue with the way that Modal is implemented
// under the hood. They are currently working on fixing it.
import Modal from 'react-bootstrap/Modal';

import Page from './utilities/Page';
import ULogo from './utilities/ULogo';
import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';
import USelectDropdown from './utilities/USelectDropdown';

import './../style/LoginStyle.css';
import ULink from './utilities/ULink';
import MultiSelect from 'react-multi-select-component'; // https://www.npmjs.com/package/react-multi-select-component

import config from './../static/config';
import UDimensionsModal from './utilities/UDimensionsModal';

export default () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [isVendor, setIsVendor] = useState(false);
    const [token, setToken] = useState('');

    const [selectedFilaments, setSelectedFilaments] = useState([]);
    const [printerMake, setPrinterMake] = useState('');
    const [printerModel, setPrinterModel] = useState('');

    const terms = <ULink to='/terms-of-service'>Terms of Service</ULink>;

    let changeDimensions = () => {
        if (xDim && yDim && zDim && selectedUnits) {
            setDimensions(xDim + selectedUnits + ' x ' + yDim + selectedUnits + ' x ' + zDim + selectedUnits);
        }
    };

    const signUp = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/register',
            data: {
                email: email,
                password: password,
                first_name: firstName,
                last_name: lastName,
                isVendor: isVendor,
            },
        }).then((response) => {
            setToken(response.data.token);
            if (isVendor) {
                registerPrinter();
            }
        });
    };

    const registerPrinter = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/printer/register',
            data: {
                vendor: 'FK -> User',
                make: 'string',
                model: 'string',
                filaments: 'string[]',
                printDimensionX: 'number',
                printDimensionY: 'number',
                printDimensionZ: 'number',
                dimensionUnit: 'string',
            },
        }).then((response) => {
            setToken(response.data.token);
            if (isVendor) {
                registerPrinter();
            }
        });
    };

    const materials = config.materials;
    const options = () => {
        let op = [];
        for (let i = 0; i < materials.length - 1; i++) {
            op.push({label: materials[i], value: materials[i]});
        }
        return op;
    };

    const getPrinterRegistrationForm = () => {
        if (isVendor) {
            return (
                <Row width='100%' className='mx-0 mb-3 width-100'>
                    <Col>
                        <span className='register-printer-title'>Register Printer</span>
                        <UTextInput
                            value={printerMake}
                            onChange={setPrinterMake}
                            onClick={() => {}}
                            placeholder='Make'
                        />
                        <UTextInput
                            value={printerModel}
                            onChange={setPrinterModel}
                            onClick={() => {}}
                            placeholder='Model'
                        />
                        <UTextInput
                            value={dimensions}
                            onChange={() => {}}
                            onClick={() => handleShow()}
                            placeholder='Build Size'></UTextInput>
                        <label /> Supported Filament
                        <MultiSelect
                            options={options()}
                            value={selectedFilaments}
                            onChange={setSelectedFilaments}
                            labelledBy={'Supported Filament'}
                        />
                    </Col>
                </Row>
            );
        } else {
            return <></>;
        }
    };

    return (
        <Page>
            <Row>
                <Col lg={6} className='mt-5'>
                    <Row className='mb-3'>
                        <Col className='ml-auto' lg={9}>
                            <span className='welcome-title'>
                                <strong>Sign Up</strong> Unite3D
                            </span>
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={firstName}
                                onChange={(e) => {
                                    setFirstName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='First Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={lastName}
                                onChange={(e) => {
                                    setLastName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Last Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={email}
                                onChange={(e) => {
                                    setEmail(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Email'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={password}
                                password
                                onChange={(e) => {
                                    setPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={confirmPassword}
                                password
                                onChange={(e) => {
                                    setConfirmPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Confirm Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <Row className='modeler-vendor-area mx-1'>
                                <Row className='mx-0 my-3 width-100'>
                                    <Col className=''>
                                        <span className=''>What is your purpose in signing up? </span>
                                        <br />
                                        <span>
                                            Print my model <strong>(Modeler)</strong>{' '}
                                            <input onChange={() => setIsVendor(false)} type='radio' name='purpose' />
                                        </span>
                                        <br />
                                        <span>
                                            Print other people's models <strong>(Vendor)</strong>{' '}
                                            <input onChange={() => setIsVendor(true)} type='radio' name='purpose' />
                                        </span>
                                    </Col>
                                </Row>
                                {getPrinterRegistrationForm()}
                                <UDimensionsModal />
                            </Row>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <input name='agree' type='checkbox' />
                            <span> Agree to&nbsp;{terms}</span>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <UButton onClick={() => signUp()}>Sign Up</UButton>
                        </Col>
                    </Row>
                </Col>
                <Col lg={4}>
                    <ULogo height={'400px'} color='purple' />
                </Col>
            </Row>
