import React from 'react';
import {useState} from 'react';
import axios from 'axios';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Page from './utilities/Page';
import ULogo from './utilities/ULogo';
import UTextInput from './utilities/UTextInput';
import UButton from './utilities/UButton';

import './../style/LoginStyle.css';
import ULink from './utilities/ULink';
import MultiSelect from 'react-multi-select-component'; // https://www.npmjs.com/package/react-multi-select-component

import config from './../static/config';
import UDimensionsModal from './utilities/UDimensionsModal';

export default () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [isVendor, setIsVendor] = useState(false);
    const [token, setToken] = useState('');

    const [selectedFilaments, setSelectedFilaments] = useState([]);
    const [printerMake, setPrinterMake] = useState('');
    const [printerModel, setPrinterModel] = useState('');

    const terms = <ULink to='/terms-of-service'>Terms of Service</ULink>;

    const useStateWithPromise = (initialState) => {
        const [state, setState] = useState(initialState);
        const resolverRef = useRef(null);

        useEffect(() => {
            if (resolverRef.current) {
                resolverRef.current(state);
                resolverRef.current = null;
            }
            /**
             * Since a state update could be triggered with the exact same state again,
             * it's not enough to specify state as the only dependency of this useEffect.
             * That's why resolverRef.current is also a dependency, because it will guarantee,
             * that handleSetState was called in previous render
             */
        }, [resolverRef.current, state]);

        const handleSetState = useCallback(
            (stateAction) => {
                setState(stateAction);
                return new Promise((resolve) => {
                    resolverRef.current = resolve;
                });
            },
            [setState],
        );

        return [state, handleSetState];
    };

    const signUp = () => {
        axios({
            method: 'post',
            url: 'http://localhost:8000/user/register',
            data: {
                email: email,
                password: password,
                first_name: firstName,
                last_name: lastName,
                isVendor: isVendor,
            },
        }).then((response) => {
            setToken(response.data.token);
            if (isVendor) {
                registerPrinter(response.data.token);
            }
        });
    };

    const registerPrinter = (theToken) => {
        let userId = '';

        axios({
            method: 'post',
            url: 'http://localhost:8000/user/verify',
            data: {
                token: theToken,
            },
        }).then((response) => {
            userId = response.data.user.id;
        });
        if (userId) {
            axios({
                method: 'post',
                url: 'http://localhost:8000/printer/register',
                data: {
                    vendor: userId,
                    make: printerMake,
                    model: printerModel,
                    filaments: selectedFilaments,
                    printDimensionX: xDim,
                    printDimensionY: yDim,
                    printDimensionZ: zDim,
                    dimensionUnit: selectedUnits,
                },
            }).then((response) => {});
        }
    };

    const materials = config.materials;
    const options = () => {
        let op = [];
        for (let i = 0; i < materials.length - 1; i++) {
            op.push({label: materials[i], value: materials[i]});
        }
        return op;
    };

    const getPrinterRegistrationForm = () => {
        if (isVendor) {
            return (
                <Row width='100%' className='mx-0 mb-3 width-100'>
                    <Col>
                        <span className='register-printer-title'>Register Printer</span>
                        <UTextInput
                            value={printerMake}
                            onChange={(e) => setPrinterMake(e.target.value)}
                            onClick={() => {}}
                            placeholder='Make'
                        />
                        <UTextInput
                            value={printerModel}
                            onChange={(e) => setPrinterModel(e.target.value)}
                            onClick={() => {}}
                            placeholder='Model'
                        />
                        <UTextInput
                            value={dimensions}
                            onChange={() => {}}
                            onClick={() => handleShow()}
                            placeholder='Build Size'></UTextInput>
                        <label /> Supported Filament
                        <MultiSelect
                            options={options()}
                            value={selectedFilaments}
                            onChange={setSelectedFilaments}
                            labelledBy={'Supported Filament'}
                        />
                    </Col>
                </Row>
            );
        } else {
            return <></>;
        }
    };

    // Modal stuff
    const [show, setShow] = useState(false);
    const [dimensions, setDimensions] = useState('');
    const [xDim, setXDim] = useState('');
    const [yDim, setYDim] = useState('');
    const [zDim, setZDim] = useState('');
    const [selectedUnits, setSelectedUnits] = useState('');

    const handleShow = () => {
        setShow(true);
        document.activeElement.blur();
    };
    const handleClose = () => {
        setShow(false);
        changeDimensions();
    };
    let changeDimensions = () => {
        if (xDim && yDim && zDim && selectedUnits) {
            setDimensions(xDim + selectedUnits + ' x ' + yDim + selectedUnits + ' x ' + zDim + selectedUnits);
        }
    };

    return (
        <Page>
            <Row>
                <Col lg={6} className='mt-5'>
                    <Row className='mb-3'>
                        <Col className='ml-auto' lg={9}>
                            <span className='welcome-title'>
                                <strong>Sign Up</strong> Unite3D
                            </span>
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={firstName}
                                onChange={(e) => {
                                    setFirstName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='First Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={lastName}
                                onChange={(e) => {
                                    setLastName(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Last Name'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={email}
                                onChange={(e) => {
                                    setEmail(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Email'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={password}
                                password
                                onChange={(e) => {
                                    setPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <UTextInput
                                value={confirmPassword}
                                password
                                onChange={(e) => {
                                    setConfirmPassword(e.target.value);
                                }}
                                onClick={() => {}}
                                placeholder='Confirm Password'
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col className='ml-auto mb-3 pr-5' lg={9}>
                            <Row className='modeler-vendor-area mx-1'>
                                <Row className='mx-0 my-3 width-100'>
                                    <Col className=''>
                                        <span className=''>What is your purpose in signing up? </span>
                                        <br />
                                        <span>
                                            Print my model <strong>(Modeler)</strong>{' '}
                                            <input onChange={() => setIsVendor(false)} type='radio' name='purpose' />
                                        </span>
                                        <br />
                                        <span>
                                            Print other people's models <strong>(Vendor)</strong>{' '}
                                            <input onChange={() => setIsVendor(true)} type='radio' name='purpose' />
                                        </span>
                                    </Col>
                                </Row>
                                {getPrinterRegistrationForm()}
                                <UDimensionsModal
                                    xDim={xDim}
                                    setXDim={setXDim}
                                    yDim={yDim}
                                    setYDim={setYDim}
                                    zDim={zDim}
                                    setZDim={setZDim}
                                    selectedUnits={selectedUnits}
                                    setSelectedUnits={setSelectedUnits}
                                    show={show}
                                    handleClose={() => handleClose}
                                />
                            </Row>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <input name='agree' type='checkbox' />
                            <span> Agree to&nbsp;{terms}</span>
                        </Col>
                    </Row>
                    <Row className='mb-3'>
                        <Col className='ml-auto pr-5' lg={9}>
                            <UButton onClick={() => signUp()}>Sign Up</UButton>
                        </Col>
                    </Row>
                </Col>
                <Col lg={4}>
                    <ULogo height={'400px'} color='purple' />
                </Col>
            </Row>
        </Page>
    );
};
